package com.nationwide.chapter4.terri;

public class ForLoop {

	/*
	 * Create a class with a for loop
	 */
	public ForLoop() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		for (int years = 2009; years <= 2016; years++) {
			System.out.println("I worked at Nationwide in " + years);

		}
	}

}
