package com.nationwide.chapter4.terri;

public class WhileLoop {

	/*
	 * Create a class with a while loop
	 */
	public WhileLoop() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		int count = 1;
		while (count < 11) {
			System.out.println("count is: " + count);
			count++;
		}
	}

}
